$inserts[] = "INSERT INTO mybb_helpdocs (hid, sid, name, description, document, usetranslation, enabled, disporder) VALUES (1, 1, 'ثبت نام کاربر', 'مزایای ثبت نام در این انجمن.', 'برای استفاده از تمامی امکانات این انجمن نیاز است که در انجمن ثبت نام نمایید. ثبت نام برای همه‌ی افراد آزاد بوده و تنها چند دقیقه به طول می‌انجامد.<br />\r\n<br />\r\nپس از ثبت نام در انجمن، شما می‌توانید به سایر اعضا پیام ارسال کنید و یا موضوع و مشکل خود را از طریق ایجاد یک موضوع در انجمن ارسال کرده و در آن‌ها نظرات شخصی خود را داشته باشید. در ضمن می‌توانید پروفایل کاربری خود را شخصی سازی کنید.<br />\r\n<br />\r\nبرخی از مزایای ثبت نام در انجمن: <br> اشتراک در موضوع‌ها، علاقمندی‌ها، تغییر پوسته‌ی انجمن، دسترسی به ویرایش‌گر مخصوص خود و ارسال ایمیل به کاربران.', 1, 1, 1);";
$inserts[] = "INSERT INTO mybb_helpdocs (hid, sid, name, description, document, usetranslation, enabled, disporder) VALUES (2, 1, 'به‌روز‌رسانی پروفایل', 'تغییر اطلاعات ثبت شده‌ی مربوط به شما.', 'گاهی اوقات ممکن است که بخواهید اطلاعات پروفایل کاربری خود را تغییر داده و به‌روز کنید. اطلاعاتی مانند: اطلاعات مربوط به پیام رسان‌ها، گذرواژه‌، یا آدرس ایمیل.<br> شما می‌توانید تمام اطلاعات کاربری خود در انجمن را از طریق کنترل پنل کاربری  تغییر دهید. برای دسترسی به کنترل پنل کاربری، تنها کافی است که بر روی عنوان کنترل پنل کاربر در منوی بالای صفحه‌ی انجمن کلیک کنید. از ستون سمت راست \'ویرایش پروفایل\'را انتخاب کرده و موارد مورد نظر خود را تغییر دهید، سپس تغییرات را ذخیره کنید تا تغییرات جدید‌ اعمال شوند.', 1, 1, 2);";
$inserts[] = "INSERT INTO mybb_helpdocs (hid, sid, name, description, document, usetranslation, enabled, disporder) VALUES (3, 1, 'استفاده از کوکی‌ها در MyBB', 'MyBB برای ذخیره‌ی اطلاعات مربوط به ثبت نام شما از کوکی‌ها استفاده می‌کند.', 'اگر در انجمن ثبت نام کرده باشید، کوکی‌ها اطلاعات مربوط به ثبت نام و ورود و خروج شما را ثبت می‌کنند. اطلاعات مربوط به آخرین بازدید شما از انجمن نیز از طریق کوکی‌ها ثبت می‌شود.<br />\r\n<br />\r\nکوکی‌ها سند‌های متنی کوچکی هستند که در رایانه‌ی شما ذخیره می‌شوند. استفاده از کوکی‌ها فقط برای موارد امنیتی بوده و استفاده‌ی دیگری ندارند. همچنین کوکی‌ها اطلاعات مربوط به موضوعات خوانده شده توسط شما را نیز ثبت می‌کنند.<br />\r\n<br />\r\nبرای حذف تمامی کوکی‌های ذخیره شده در این انجمن  <a href=\"misc.php?action=clearcookies&amp;my_post_key={1}\"> اینجا </a>کلیک کنید.', 1, 1, 3);";
$inserts[] = "INSERT INTO mybb_helpdocs (hid, sid, name, description, document, usetranslation, enabled, disporder) VALUES (4, 1, 'ورود و خروج', 'روش ورود و خروج از سیستم.', 'زمان ورود شما به این انجمن  کوکی‌ها فعال می‌شوند، اطلاعات ورود شما را ثبت کرده و برای مراجعه‌ی بعدی به صورت خودکار وارد انجمن می‌شوید و نیازی به وارد کردن نام کاربری و گذرواژه‌  نخواهید داشت . با استفاده از کوکی‌ها غیر از شما هیچ فرد دیگری نمی‌تواند وارد حساب کاربری شما در این انجمن شود.<br />\r\n<br />\r\nاگر پس از ورود به انجمن از حساب کاربری خود خارج شوید کوکی‌ها برای شما هنگام بازدید مجدد اجرا نمی‌شوند.<br />\r\n<br />\r\n هنگام ورود شما به سیستم انجمن، به طور خودکار یک کوکی در سیستم شما تنظیم می‌شود و به شما اجازه می‌دهد بدون آنکه هر بار نام کاربری و گذرواژه‌ی خود را وارد کنید، قسمت‌های مختلف انجمن را مرور کنید . خروج از انجمن باعث حذف شدن آن کوکی می‌شود تا مطمئن شوید که شخص دیگری از حساب کاربری شما در انجمن‌ها استفاده نمی‌کند.\r\n<br /><br />\r\n برای ورود به سیستم از قسمت \'ورود\' در بالای صفحه استفاده کنید و اگر قبلاً وارد شده‌اید و می‌خواهید از سیستم خارج شوید، از همان مکان یعنی  بالای انجمن برای این کار استفاده کنید. در ضمن اگر نمی‌توانید از سیستم خارج شوید، پاک کردن کوکی‌ها نتیجه‌ای مشابه دارد.\r\nاگر پس از ورود به انجمن از حساب کاربری خود خارج شده باشید، هنگام بازدید بعدی، کوکی‌ها برای شما اجرا نخواهند شد.', 1, 1, 4);";
$inserts[] = "INSERT INTO mybb_helpdocs (hid, sid, name, description, document, usetranslation, enabled, disporder) VALUES (5, 2, 'ایجاد یک موضوع جدید‌', 'نحوه‌ی ایجاد موضوع جدید در یک انجمن.', 'اگر در انجمن مورد نظر خود قصد ایجاد موضوع جدیدی را دارید،  در قسمت بالا و یا پایین صفحه، بر روی گزینه‌ی \'ایجاد موضوع جدید\' کلیک کنید. لطفاً توجه کنید که ممکن است در بعضی از انجمن‌ها شما اجازه‌ی ایجاد موضوع جدیدی را نداشته باشید. ممکن است بعضی کاربران این اختیار را نداشته باشند و یا به طور کلی مدیر سایت ارسال موضوع جدید‌ را در آن انجمن غیرفعال کرده باشد و انجمن بایگانی شده باشد.', 1, 1, 1);";
$inserts[] = "INSERT INTO mybb_helpdocs (hid, sid, name, description, document, usetranslation, enabled, disporder) VALUES (6, 2, 'ارسال پاسخ', 'حوه‌ی ارسال پاسخ به یک موضوع.', 'هنگام مشاهده‌ی انجمن‌ها، ممکن است بخواهید به موضوعی پاسخ دهید. برای این کار می‌توانید روی گزینه‌ی \'پاسخ جدید\' در پایین یا بالای موضوع کلیک کنید. لطفاً توجه داشته باشید که ممکن است مدیر سایت ارسال پاسخ جدید‌ را برای برخی از کاربران محدود کرده باشد.<br />\r\n<br />\r\nدر ضمن ممکن است مدیر  موضوع را بسته باشد؛ در این صورت کاربران نمی‌توانند به آن موضوع پاسخ دهند. در هیچ حالتی یک کاربر بدون کمک گرفتن از مدیر بخش یا مدیر‌کل سایت نمی‌تواند  موضوع بسته شده را دوباره باز کند!', 1, 1, 2);";
$inserts[] = "INSERT INTO mybb_helpdocs (hid, sid, name, description, document, usetranslation, enabled, disporder) VALUES (7, 2, 'مای‌کد', 'راهنمای استفاده از مای‌کد در ارسال‌ها.', 'شما می‌توانید از مای‌کد  در ارسال‌هایتان استفاده کنید تا به آن‌ها شکل دهید، مای‌کد نسخه‌ی ساده شده‌ای از زبان HTML می‌باشد.\r\n<p><br />\r\n[b]این متن درشت است[/b]<br />\r\n&nbsp;&nbsp;&nbsp;<b>این متن درشت است</b>\r\n<p>\r\n[i]این متن مایل است[/i]<br />\r\n&nbsp;&nbsp;&nbsp;<i>این متن مایل است</i>\r\n<p>\r\n[u]این متن زیرخط دار است[/u]<br />\r\n&nbsp;&nbsp;&nbsp;<u>این متن زیرخط دار است</u>\r\n<p><br />\r\n[url]http://www.mybbiran.com/[/url]<br />\r\n&nbsp;&nbsp;&nbsp;<a href=\"http://www.mybbiran.com/\">http://www.mybbiran.com/</a>\r\n<p>\r\n[url=http://www.mybbiran.com/]MybbIran.com[/url]<br />\r\n&nbsp;&nbsp;&nbsp;<a href=\"http://www.mybbiran.com/\">MybbIran.com</a>\r\n<p>\r\n[email]translator@mybbiran.com[/email]<br />\r\n&nbsp;&nbsp;&nbsp;<a href=\"mailto: translator@mybbiran.com\">translator@mybbiran.com</a>\r\n<p>\r\n[email=translator@mybbiran.com]ارسال ایمیل به مترجم[/email]<br />\r\n&nbsp;&nbsp;&nbsp;<a href=\"mailto: translator@mybbiran.com\">ارسال ایمیل به مترجم</a>\r\n<p>\r\n[email=translator@mybbiran.com?subject=spam]ارسال ایمیل با موضوع به مترجم[/email]<br />\r\n&nbsp;&nbsp;&nbsp;<a href=\"mailto: translator@mybbiran.com?subject=spam\">ارسال ایمیل با موضوع به مترجم</a>\r\n<p><br />\r\n[quote]متن نقل قول شده[/quote]<br />\r\n&nbsp;&nbsp;&nbsp;<quote>متن نقل قول شده</quote>\r\n<p>\r\n[code]کد قالب بندی شده[/code]<br />\r\n&nbsp;&nbsp;&nbsp;<code>کد قالب بندی شده</code>\r\n<p><br />\r\n[img]http://www.php.net/images/php.gif[/img]<br />\r\n&nbsp;&nbsp;&nbsp;<img src=\"http://www.php.net/images/php.gif\">\r\n<p>\r\n[img=50x50]http://www.php.net/images/php.gif[/img]<br />\r\n&nbsp;&nbsp;&nbsp;<img src=\"http://www.php.net/images/php.gif\" width=\"50\" height=\"50\">\r\n<p><br />\r\n[color=red]متن قرمز[/color]<br />\r\n&nbsp;&nbsp;&nbsp;<font color=\"red\">متن قرمز</font>\r\n<p>\r\n[size=3]اندازه‌ی 3[/size]<br />\r\n&nbsp;&nbsp;&nbsp;<font size=\"3\">اندازه‌ی 3</font>\r\n<p>\r\n[font=Tahoma]قلم این متن Tahoma است.[/font]<br />\r\n&nbsp;&nbsp;&nbsp;<font face=\"Tahoma\">قلم این متن Tahoma است.</font>\r\n<p><br />\r\n[align=center]متن وسط چین[/align]<div align=\"center\">متن وسط چین</div>\r\n<p>\r\n[align=left]متن چپ چین[/align]<div align=\"left\">متن چپ چین</div>\r\n<p><br />\r\n[list]<br />\r\n[*]گزینه‌ی #1<br />\r\n[*]گزینه‌ی #2<br />\r\n[*]گزینه‌ی #3<br />\r\n[/list]<br />\r\n<ul>\r\n<li>گزینه‌ی #1</li>\r\n<li>گزینه‌ی #2</li>\r\n<li>گزینه‌ی #3</li>\r\n</ul><p><font size=1>شما می‌توانید با استفاده از [list=1] یک فهرست شماره دار ایجاد کنید، و با استفاده از [list=a] یک فهرست الفبایی ایجاد کنید.</size>', 1, 1, 3);";

